import { Component } from '@angular/core';
import { Student } from '../student.model';

@Component({
  selector: 'app-student-list',
  templateUrl: './student-list.component.html',
  styleUrls: ['./student-list.component.scss']
})
export class StudentListComponent {

  Students: Student[] = [
    { name: "Ron", subject: "math", average: 99 },
    { name: "Sara", subject: "programming", average: 55 },
    { name: "Rachel", subject: "pysichology", average: 88 }
  ];

  selectedStudent: Student | null = null;

  DeleteFunc(student: Student) {
    let indexToDelete = this.Students.indexOf(student);
    this.Students.splice(indexToDelete, 1);
  }

  ShowDetails(studentToShow: Student) {
    this.selectedStudent = studentToShow;
  }

  ShowNewStudentDetails() {
    this.selectedStudent = new Student('new student');
  }

  addNewStudent(studentToAdd: Student) {
    this.Students.push(studentToAdd);
    this.selectedStudent = null;  // מאפס את הסטודנט שנבחר
  }

}
<ul class="list-group">
    <li *ngFor="let student of Students" class="list-group-item"
    [ngClass]="{'active':!(selectedStudent && selectedStudent.name == student.name),
        'list-group-item-success':student.average>90
    }"
    >
        <div class="row">
             <div class="col-1">{{student.name}}</div>
             <div class="col-3">{{student.subject}}</div>
             <div class="col-2">
                <button (click)="ShowDetails(student)">Show Student Details</button> 
            </div>
             <div class="col-2">
                <button (click)="DeleteFunc(student)">Delete Student</button>
             </div>
        </div>

    </li>
</ul>
<br>
<button class="btn btn-primary" (click)="ShowNewStudentDetails()">Add New Student</button>

<br><br>

