import { Component } from '@angular/core';
import { Student } from '../student.model';

@Component({
  selector: 'app-student-list',
  templateUrl: './student-list.component.html',
  styleUrls: ['./student-list.component.scss']
})
export class StudentListComponent {

  Students: Student[] = [{name:"Ron" , subject:"math"},{name:"Sara" , subject:"programming"},{name:"Rachel" , subject:"pysichology"}]
  DeleteFunc(student:Student){
  let indexToDelete = this.Students.indexOf(student)
  this.Students.splice(indexToDelete,1);
  };

 selectedStudent!: Student; 

  ShowDetails(studentToShow:Student){
   this.selectedStudent = studentToShow
  }


}
<ul class="list-group">
    <li *ngFor="let student of Students" class="list-group-item">
        <div class="row">

            <form>
                <div class="mb-3">
                  <label for="exampleInputEmail1" class="form-label">Email address</label>
                  <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp">
                  <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
                </div>
                <div class="mb-3">
                  <label for="exampleInputPassword1" class="form-label">Password</label>
                  <input type="password" class="form-control" id="exampleInputPassword1">
                </div>
                <div class="mb-3 form-check">
                  <input type="checkbox" class="form-check-input" id="exampleCheck1">
                  <label class="form-check-label" for="exampleCheck1">Check me out</label>
                </div>
                <button type="submit" class="btn btn-primary">Submit</button>
              </form>



             <div class="col-1">{{student.name}}</div>
             <div class="col-3">{{student.subject}}</div>
             <div class="col-2">
                <button (click)="ShowDetails(student)">Show Student Details</button> 
            </div>
             <div class="col-2">
                <button (click)="DeleteFunc(student)">Delete Student</button>
             </div>
        </div>

    </li>
</ul>
<br>
<app-student-details [student]="selectedStudent"></app-student-details>
